function Ground(t,i,h,e){this.width=t,this.height=i,this.x=h,this.y=e}function Chicken(t,i,h,e){this.width=t,this.height=i,this.x=h,this.y=e}function Raccoon(t,i,h,e){this.width=t,this.height=i,this.x=h,this.y=e}Ground.prototype.render=function(t){t.fillStyle="green",t.fillRect(this.x,this.y,this.width,this.height)},Chicken.prototype.render=function(t){t.fillStyle="white",t.fillRect(this.x,this.y,this.width,this.height)},Raccoon.prototype.render=function(t){t.fillStyle="black",t.fillRect(this.x,this.y,this.width,this.height)};